package transaction

import (
	"encoding/hex"
	"testing"

	"github.com/nspcc-dev/neo-go/pkg/config/netmode"
	"github.com/stretchr/testify/assert"
)

var (
	// tx from testnet affad44bb6acacabc058db0bf1e12ab1239ae5e04007b4d4a2ea0cda868e284a
	rawInvocationTX = ""
)

func decodeTransaction(rawTX string, t *testing.T) *Transaction {
	b, err1 := hex.DecodeString(rawTX)
	assert.Nil(t, err1)
	tx, err := NewTransactionFromBytes(netmode.TestNet, b)
	assert.NoError(t, err)
	return tx
}
